mapper.select:
SELECT * FROM seawall
AND seaWallType = #{seaWallPageDTO.seaWallType}
AND title like '%${seaWallPageDTO.keywords}%'
and delete_flag = 0
order by create_time desc limit #{seaWallPageDTO.pageStart}, #{seaWallPageDTO.pageSize}

mapper.update:
update seawall
set delete_flag = 1
where id = #{id}
